import FormioUtils from 'formiojs/utils';
import html2pdf from 'html2pdf.js'; //import '@/assets/styles/report.scss'

import { CurrencyRender, DataGridRender, TextFieldRender, TextAreaRender, NumberRender } from '@/utils/render';
import { Data, Form } from '@/utils/test';
export default {
  components: {
    'r-currency': CurrencyRender,
    'r-datagrid': DataGridRender,
    'r-number': NumberRender,
    'r-textfield': TextFieldRender,
    'r-textarea': TextAreaRender
  },
  data: function data() {
    return {};
  },
  computed: {
    data: function data() {
      return Data.data;
    },
    form: function form() {
      return Form;
    }
  },
  methods: {
    getComponentType: function getComponentType(key) {
      var component = FormioUtils.getComponent(this.form.components, key);
      return component.type;
    },
    getComponentLabel: function getComponentLabel(key) {
      var component = FormioUtils.getComponent(this.form.components, key);
      return component.label;
    },
    getComponents: function getComponents(key) {
      var component = FormioUtils.getComponent(this.form.components, key); // console.log(this.data[key])
      // console.log(component.components)

      return component.components;
    },
    isComponentInput: function isComponentInput(key) {
      var component = FormioUtils.getComponent(this.form.components, key);
      return component.input;
    },
    downloadPDF: function downloadPDF() {
      console.log('rendering html2pdf...');
      html2pdf(document.getElementById('report-content'), {
        margin: 10,
        filename: 'assessment1.pdf',
        html2canvas: {
          dpi: 192,
          letterRendering: true
        },
        jsPDF: {
          unit: 'mm',
          format: 'a4',
          orientation: 'portrait'
        }
      });
    }
  }
};
