var items = ['AG-breed-mainBreedsCattle', 'AG-breed-mainBreedsBuffalo', 'AG-breed-mainBreedsHorses', 'AG-breed-mainBreedsCamels', 'AG-breed-mainBreedsSheep', 'AG-breed-mainBreedsGoats', 'AG-breed-mainBreedsPigs', 'AG-breed-mainBreedsPoultry', 'AG-breed-mainBreedsBees', 'AG-breed-mainBreedsSilkworms', 'AG-breed-mainBreedsFish'];
var groupList = [];

for(var j =0; j<items.length; j++){
var item = items[j];
var arrayField = data[item];

if(typeof arrayField != "undefined"){
	if (!(groupList.indexOf(arrayField) > -1)){
		groupList.push(arrayField);
	}
}
}

if (groupList.length  == 1){
value = 0;
}else if (groupList.length  ==2){
value = 5;
}else if (groupList.length  >=3){
value = 10;
}

var items = ['AG-breed-mainBreedsCattle', 'AG-breed-mainBreedsBuffalo', 'AG-breed-mainBreedsHorses', 'AG-breed-mainBreedsCamels', 'AG-breed-mainBreedsSheep', 'AG-breed-mainBreedsGoats', 'AG-breed-mainBreedsPigs', 'AG-breed-mainBreedsPoultry', 'AG-breed-mainBreedsBees', 'AG-breed-mainBreedsSilkworms', 'AG-breed-mainBreedsFish'];
var groupList = [];

for(var j =0; j<items.length; j++){
var item = items[j];
var arrayField = data[item];

if(typeof arrayField != "undefined"){
	if (!(groupList.indexOf(arrayField) > -1)){
		groupList.push(arrayField);
	}
}
}

var items2 = ['AG-breed-breedingAnimalsCattle', 'AG-breed-breedingAnimalsBuffalo', 'AG-breed-breedingAnimalsHorses', 'AG-breed-breedingAnimalsCamels', 'AG-breed-breedingAnimalsSheep', 'AG-breed-breedingAnimalsGoats', 'AG-breed-breedingAnimalsPigs', 'AG-breed-breedingAnimalsPoultry', 'AG-breed-breedingAnimalsBees', 'AG-breed-breedingAnimalsSilkworms', 'AG-breed-breedingAnimalsFish'];
var score = 0;

for(var j =0; j<items2.length; j++){
var item2 = items2[j];
var arrayField2 = data[item2];

if(typeof arrayField2 != "undefined"){

		if(arrayField2 === "yes"){
			score = score + 10;
		}

}
}

value = parseFloat(Math.round((score/groupList.length) * 100) / 100).toFixed(2);

var nAcademics = (parseFloat(data['AG-breed-ac-a']) || parseFloat(data['AG-breed-ac-a']) == 0 ? 1: 0) + (parseFloat(data['AG-breed-ac-b']) || parseFloat(data['AG-breed-ac-b']) == 0 ? 1: 0);

var sumAcademic = (parseFloat(data['AG-breed-ac-a']) || 0) + (parseFloat(data['AG-breed-ac-b']) || 0);

value = parseFloat(Math.round((sumAcademic/nAcademics) * 100) / 100).toFixed(2);

